{
  "glossary": {
    "dependency": {
      "definition": "A piece of software or library that another piece of software relies on to function.",
      "vibecoder": "Like a crutch for a runner; without it, they might stumble."
    },
    "hook": {
      "definition": "A special function that allows you to 'hook into' React's lifecycle events.",
      "vibecoder": "Think of it as a fishing line; it lets you catch moments in your app's journey."
    },
    "schema": {
      "definition": "A blueprint that defines the structure of data in a database.",
      "vibecoder": "Like a map for a treasure hunt; it shows you where everything is supposed to go."
    },
    "middleware": {
      "definition": "Software that acts as a bridge between different applications or services.",
      "vibecoder": "Imagine a traffic cop directing cars at a busy intersection; it helps manage the flow."
    },
    "selector": {
      "definition": "A function that extracts specific pieces of data from a larger state.",
      "vibecoder": "Like a chef picking the best ingredients from a pantry; it finds what you need."
    },
    "component": {
      "definition": "A reusable piece of code that represents a part of a user interface.",
      "vibecoder": "Think of it as a LEGO block; you can build something great by combining them."
    },
    "import": {
      "definition": "A way to bring in code or libraries from other files or modules.",
      "vibecoder": "Like opening a door to a new room filled with tools you can use."
    },
    "job": {
      "definition": "A task or unit of work that needs to be completed.",
      "vibecoder": "Like a bee collecting nectar; each job contributes to the hive's success."
    },
    "configuration": {
      "definition": "Settings that determine how a program or system operates.",
      "vibecoder": "Think of it as tuning a musical instrument; it ensures everything sounds just right."
    },
    "context": {
      "definition": "Information about the current situation or environment in which code is running.",
      "vibecoder": "Like the atmosphere at a party; it influences how everyone behaves."
    },
    "database model": {
      "definition": "A structure that defines how data is organized and accessed in a database.",
      "vibecoder": "Imagine a filing cabinet; it organizes documents for easy retrieval."
    },
    "API": {
      "definition": "A set of rules that allows different software applications to communicate with each other.",
      "vibecoder": "Like a waiter taking your order; it connects you to the kitchen without you going in."
    },
    "action": {
      "definition": "An event or command that triggers a change in the application state.",
      "vibecoder": "Think of it as a spark that ignites a fire; it sets things in motion."
    },
    "provider": {
      "definition": "A component that supplies data or functionality to other components.",
      "vibecoder": "Like a gardener providing water to plants; it nurtures growth."
    },
    "environment variable": {
      "definition": "A dynamic value that can affect the behavior of running processes.",
      "vibecoder": "Imagine the weather; it changes how you dress and act each day."
    },
    "reducer": {
      "definition": "A function that takes the current state and an action to produce a new state.",
      "vibecoder": "Like a sculptor chiseling away stone to reveal a statue; it shapes the final form."
    },
